INSERT INTO `dmt_brand` VALUES (1, '666777', '66666666666677', '&lt;p&gt;666666666666666666666666666677&lt;/p&gt;', 1, 1507684989);
INSERT INTO `dmt_brand` VALUES (2, '777', '777777777777777777777777', '&lt;p&gt;777777777777777777&lt;/p&gt;', 1, 1507685873);
INSERT INTO `dmt_brand` VALUES (3, '百年德欣堂，真中医 治顽疾', '郑州德欣堂中医其前身为德欣堂医馆，始建于公元1918年（民国7年），由清末太医院河南籍太医史平道创办。民国初期军阀混战，硝烟四起，原本告老还乡颐养天年的史平道看到家乡民众被瘟疫肆虐，便倾尽家财为灾民医病送药，时任河南省长赵倜为其题字“德行天下，欣愿安康”，并取其“德欣”二字成立了当时河南省最大医疗机构——德欣堂医馆。', '&lt;p&gt;&amp;nbsp; &amp;nbsp; 郑州德欣堂中医其前身为德欣堂医馆，始建于公元1918年（民国7年），由清末太医院河南籍太医史平道创办。民国初期军阀混战，硝烟四起，原本告老还乡颐养天年的史平道看到家乡民众被瘟疫肆虐，便倾尽家财为灾民医病送药，时任河南省长赵倜为其题字“德行天下，欣愿安康”，并取其“德欣”二字成立了当时河南省最大医疗机构——德欣堂医馆。&lt;span style=&quot;font-family:微软雅黑;font-family: 微软雅黑;&quot;&gt;省长的支持加上史平道本人的人品与威望，德欣堂医馆吸引了诸多名医的到来，短短数年德欣堂医馆在中原地区几乎人尽皆知，不仅为百姓造福，也积累了诸多救世良方。遗憾的是刚刚成立&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 14px; display: inline !important;&quot;&gt;20年的德欣堂医馆，由于中日战争的爆发及花园口事件在1938年被毁。医馆被毁使得史平道先生郁郁而终，临终前他嘱托众弟子，一定要保存好20年来众多名医留下的验方，待时机成熟时重开医馆。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 14px; display: inline !important;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 弹指一挥间&lt;/span&gt;80年过去了，那些往事早已经被世人所遗忘，但是年近九旬的万泓晟教授，从未忘记过从小跟虽父亲行医时就给他讲起的关于史从道与德欣堂医馆的故事，自己也依稀记得一些史师爷的音容笑貌。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;近年来国家号召中医回归传统，万泓晟教授兴奋不已，是时候重开德欣堂医馆了。功夫不负有心人&lt;/span&gt;2017年7月1日我国首部中医药法出台的同时德欣堂中医馆也重新开业了。万泓晟教授将父亲留给他的数百张老医馆的验方无偿的赠与德欣堂中医馆，让年轻的医生们参考学习，百十年前的药方让很多学习现代中医的医生感到震惊，配伍之绝妙，搭配之合理，取材之考究，完全彰显出了中医药学的完美。&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;德欣堂秉承百年前的医馆宗旨，&lt;/span&gt;“德行天下，欣愿安康”以治疗全科常见病为基础，以百年验方为特色，集中医诊疗、预防、保健、康复为一体，坚持“实实在在用真心做中医，本本分分凭良心配好药”的原则，特聘请河南中医学院及省市级知名专家教授长期坐诊，秉承五千年中医中药精髓，运用中医“内治+外疗”服务广大患者。齐心协力为每位患者定制最好的诊断治疗。&lt;/span&gt;&lt;span style=&quot;text-indent: 0px; font-family: 微软雅黑; font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 37px; display: inline !important;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;德欣堂始终坚持从原产地采购优质中药，并由经验丰富的药师严格把关，保证药品地道，并且优中选优。所有汤药都由资深煎药师专一负责，抓药、浸泡、煎汤、榨汁、滤渣、灌装，一丝不苟，严格把握煎药时间及火候，为您呈现纯正、地道的药汤。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;text-indent: 0px; font-family: 微软雅黑; font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 37px; display: inline !important;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:微软雅黑&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; 德欣堂以高质量、高水平、人性化、个性化的医疗服务，保持以病人为中心、以质量为核心，以百年验方为优势的医疗服务理念，坚持&lt;/span&gt;“医术精、服务优、价格低、百姓爱”，做老百姓身边中医馆。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;text-indent: 0px; font-family: 微软雅黑; font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 37px; display: inline !important;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 14px; display: inline !important;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑; font-size: 14px; display: inline !important;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1516263648);

-- ----------------------------
-- Table structure for dmt_equipment
-- ----------------------------
DROP TABLE IF EXISTS `dmt_equipment`;
CREATE TABLE `dmt_equipment`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  `sid` tinyint(4) NOT NULL,
  `username` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `cycle` float(4, 1) NOT NULL,
  `content` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `show` tinyint(4) NULL DEFAULT 1,
  `pic` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `addtime` int(11) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 17 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of dmt_equipment
-- ----------------------------
INSERT INTO `dmt_equipment` VALUES (1, 11, '张女士', 3.0, '我的支气管炎现在已经好了，在没用御灵菲宝之前，真是一言难尽，各种药都试了就是没效果。', 1, '20171018/befa178e659b27ebe3e4ac9aaa33247f.png', 1508292838);
INSERT INTO `dmt_equipment` VALUES (2, 11, '李先生', 5.0, '我爷爷多年的气管炎，吃药就会好，停药就复发，后来亲戚介绍的御灵菲宝，没想到坚持用了一段时间后，停药也没事了，现在身体状况很好。', 1, '20171018/d371e772f909ad3a6ff419bd09da9428.png', 1508292912);
INSERT INTO `dmt_equipment` VALUES (3, 1, '王女士', 2.0, '我妈腰间盘突出，什么法都试过没用，手术也做过，半年就复发了，听朋友说百孝贴不错，就买来试试，贴了十多天腰也不酸不困了，我就让妈妈坚持用。', 1, '20171018/02c05f7696c3fd34ac8a271fa47fddad.png', 1508292931);
INSERT INTO `dmt_equipment` VALUES (4, 1, '张先生', 5.0, '老家的大舅患有腰间盘突出 听他们说这个黑膏药百孝贴效果挺好， 就给他推荐了 上个星期大舅还给我打电话说腰突缓解了不少，能下床了。', 1, '20171018/8cc55efbbfe60be088eb3c8eec0192fe.png', 1508292997);
INSERT INTO `dmt_equipment` VALUES (5, 11, '孙先生', 4.0, '我哥患哮喘病，经常发作，吃了很多药，偏方也试了，没效果。后来听同事说御灵菲宝，就买来试试，没想到这个药还真行，哮喘已经很长时间没发作了。', 1, '20171018/b2263e3359f4d80d6e85fc9d1900559d.png', 1508292977);
INSERT INTO `dmt_equipment` VALUES (6, 1, '刘同学', 3.0, '病例\r\n小刘同学5年前打篮球的时候膝盖扭伤，当时没有在意，半年后膝盖疼痛厉害，去检查才知道半月板已经损伤，大夫说这种半月板组织一旦损伤很难恢复，小刘非常喜欢运动，现在却无能为力，期间也试过很多的调理办法，针灸、推拿，到头来没有什么作用，因为这种手术费用太昂贵，而且恢复率也很低，一直没有手术，结果越来越严重。\r\n\r\n治疗\r\n半月板损伤多由扭转外力引起，当一腿承重，小腿固定在半屈曲、外展位时，身体猛然内旋，内侧半月板在股骨昱胫骨之间受到旋转压力，而导致半月板撕裂。常见人群为运动员、军人、体力劳动者等。半月板损', 1, '20171018/23af0514403b20573ccb46f0aa370451.png', 1508293021);
INSERT INTO `dmt_equipment` VALUES (7, 1, '赵先生', 6.0, '病例\r\n今年35岁的赵先生，多年来从事平面设计工作，一直以来都觉得自己的身体挺不错的。多年的辛苦工作终于买了新房，有了属于自己的家。今年年初搬新家的时候，将一个冰箱从一楼搬到五楼后，就感觉自己的腰有点不对劲了，有点疼，当时没在意，觉得没有什么大问题。刚开始只是腰两边疼，直不起腰，慢慢的越来越严重，逐渐沿大腿放射性疼痛，走不了100米。到医院一检查，说是腰椎骨质增生、腰椎间盘L4-5突出，建议我手术，我有顾忌就没有同意。\r\n后来实在疼的受不了，就考虑做手术，但经过了解，赵先生害怕手术风险，决定保守治疗。随后', 1, '20171018/a0d8a9c20892c7a993d58e4b562f812d.png', 1508293045);
INSERT INTO `dmt_equipment` VALUES (8, 1, '张帅', 10.0, '病例\r\n张帅在高中的时候被查出患有类风湿关节炎。起初手指关节僵硬，疼痛，并没有在意。后无法忍受关节疼痛，到当地医院检查，诊断出类风湿关节炎。回家后吃止痛药缓解病情。但没有得到有效治疗，病情越来越严重。关节受累较多，且部分关节有畸形。\r\n\r\n治疗\r\n张帅母亲带他来德民堂中医馆咨询就诊，看到张帅全身严重浮肿，面部严重潮红，四肢关节严重变形歪斜，并且右手肘关节骨质变形增生成竹笋状骨刺，专家团队诊断他为类风湿性关节炎，并且立即制定了一套治疗方案。采用中西医结合，根据一定比例量身定制了药物，经过一段时间的治疗后，目', 1, '20171018/f91f0f24bd09b908974217b06a1e483b.png', 1508293092);
INSERT INTO `dmt_equipment` VALUES (9, 5, '李华', 5.0, '多年慢性胃炎终于痊愈了\r\n今年40岁的李先生患有慢性胃炎有好几年了，这些年的日子真是折磨，经常胃疼，吃不下饭，原来180斤的体重变成了130斤，因为长期身体难受，精神也很差劲，整个人看起来病恹恹的。\r\n  李先生来到德民堂中医馆时，本来就没报什么希望，是家人非要让他来试试的。据李先生说，他这些年因为胃炎看了好多医院和医生，可都是只管用一段时间，一停药就又开始难受了，他已经心灰意冷了。而且因为常年吃西药，现在已经对他没有一点作用了。\r\n    德民堂中医馆的中医师从中医的角度，详细询问了李先生患胃炎的过程，', 1, '20171018/2390655899a10511c05f880ac0b24e63.png', 1508293221);
INSERT INTO `dmt_equipment` VALUES (10, 5, '孙先生', 2.0, '胃溃疡是多发病、慢性病，易反复发作，呈慢性经过，因而要治愈胃溃疡，需要一个较为艰难持久的历程。\r\n    三年前小孙在一次聚会上正喝酒聊天，突然腹部剧烈疼痛难忍，大家赶紧把他送到了医院，经过检查后确诊为胃溃疡伴有幽门螺杆菌感染患者。在医院住了一段时间，医生进行杀菌治疗，吃了好多抗生素，症状减轻后就出院了。\r\n    本来以为没事了，可是之后一旦忘了吃饭或者是吃了刺激性的食物，小孙就开始胃痛，反复发作，就继续吃抗生素治疗，但还是偶尔胃不消化，不舒服。\r\n    前一段时间经朋友介绍来德民堂中医馆想看看，关于', 1, '1111', 1507794524);
INSERT INTO `dmt_equipment` VALUES (11, 5, '张女士', 1.0, '张女士经常胃痛，无食欲，饭后即胃脘饱胀，打嗝，嗳气，经过查胃镜，诊断为隆起糜烂性胃炎。后经过治疗服用了很多药物，效果不明显。\r\n   张女士来德民堂中医馆就诊时，面色无华，精神不振。中医认为，张女士的胃炎多因长期情志不遂，饮食不节，劳逸失常，导致肝气郁结，脾失健运，胃脘失和，日久中气亏虚，从而引发种种症状。中医师建议根据本病的病因、临床症状及舌脉表现辨证治疗，让张女士坚持服药，同时养成良好的生活习惯，忌烟酒、少吃辛辣、烧烤等刺激性食物，经过一个疗程的治疗后，张女士表示症状几乎没有了。', 1, '20171018/00cfd7241cecd6dfa79620b7583b0a6c.png', 1508293326);
INSERT INTO `dmt_equipment` VALUES (12, 5, '张丽', 2.0, '案例\r\n张女士经常胃痛，无食欲，饭后即胃脘饱胀，打嗝，嗳气，经过查胃镜，诊断为隆起糜烂性胃炎。后经过治疗服用了很多药物，效果不明显。\r\n\r\n治疗\r\n张女士来德民堂中医馆就诊时，面色无华，精神不振。中医认为，张女士的胃炎多因长期情志不遂，饮食不节，劳逸失常，导致肝气郁结，脾失健运，胃脘失和，日久中气亏虚，从而引发种种症状。中医师建议根据本病的病因、临床症状及舌脉表现辨证治疗，让张女士坚持服药，同时养成良好的生活习惯，忌烟酒、少吃辛辣、烧烤等刺激性食物，经过一个疗程的治疗后，张女士表示症状几乎没有了。', 1, '20171018/bd2c79eabbf5d8a3c5b55f8ee5eda3b6.png', 1508293310);
INSERT INTO `dmt_equipment` VALUES (13, 11, '张妈妈', 6.0, '慢性支气管炎\r\n\r\n病例\r\n张女士的母亲今年刚过60岁，年轻时一到冬天就容易咳嗽、气喘，最近两年越来越严重了，特别是到了晚上不停地咳嗽。医院检查说是慢性支气管炎、肺气肿、肺心病。张女士姊妹三个四处带着母亲看病，看到能治疗这个病的药就买。时好时坏，有些药吃上能好点，停药后就更严重了。现在越来越严重了，咳嗽、咳痰，严重时还咳血，不敢活动稍微一动就喘的厉害，胸闷，呼吸困难。\r\n\r\n治疗\r\n支气管炎初期治疗很容易治愈，但是如果长期没有得到有效治疗，或者治疗进入误区就会引发肺部损伤及呼吸粘膜糜烂，西医的治疗方法就是', 1, '20171018/f390599ebc2bb1ed901e7faa15da43bd.png', 1508293292);
INSERT INTO `dmt_equipment` VALUES (14, 11, '郭阿姨', 10.0, '支气管哮喘\r\n\r\n病例\r\n小郭的奶奶今年60多了，患有多年的支气管哮喘，一发作起来咳的很厉害，胸闷气短，脸都通红。夜间支气管哮喘反应更是严重，在医院打吊瓶也快半个月不见好转，小郭看到老人家受这罪心里不是滋味，带爷爷去看过大医院，都说支气管哮喘不好根治。后来辗转打听到德民堂中医馆。\r\n\r\n治疗\r\n    支气管哮喘是一种气道慢性非特异性炎症，由多种细胞和细胞组分参与的气道慢性炎症性疾病，这种慢性炎症与气道高反应性相关，通常出现广泛而多变的可逆性气流受限，导致反复发作的喘息、气促、胸闷和（或）咳嗽等症状，多在', 1, '20171018/c6644758ae79c1c91a8cdd57d783dc01.png', 1508293269);
INSERT INTO `dmt_equipment` VALUES (15, 11, '王阿姨', 5.0, '肺气肿\r\n\r\n病例\r\n     王阿姨最近经常感觉气短，走一段路就呼吸不畅，还感到乏力、体重下降、食欲减退、上腹胀满，伴有咳嗽、咳痰等症状，去医院检查结果为阻塞性肺气肿。\r\n\r\n治疗\r\n肺气肿是多种疾病均可表现出来的病理改变，需要针对原发疾病进行治疗。德民堂中医馆对此类疾病的治疗方法是先为老人补充正规的，专门针对呼吸系统问题的免疫力，这样就能还能增加气管、支气管的粘液屏障功能，在支气管粘膜层形成保护层，提高从咽喉到气管和肺部的免疫机能，阻止细菌进入呼吸系统，从而阻止肺气肿恶化。\r\n经过一个疗程治疗，王阿姨', 1, '20171018/ef313c7bef355592264114dd51b31d56.png', 1508293246);

-- ----------------------------
-- Table structure for dmt_feature
-- ----------------------------
DROP TABLE IF EXISTS `dmt_feature`;
CREATE TABLE `dmt_feature`  (
  `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `digest` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `cate` tinyint(4) NULL DEFAULT NULL COMMENT '1为中医保健,2为疾病预防,3为养生食疗',
  `status` tinyint(4) NULL DEFAULT 1,
  `addtime` int(11) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 20 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of dmt_feature
-- ----------------------------
INSERT INTO `dmt_feature` VALUES (8, '肺心病日常保健', '是长期咳嗽、咯痰及不同程度的呼吸困难，特别是活动后或在阴冷季节里症状更为明显。在心肺功能代偿期，病人安静时可以没有症状，一旦稍微活动，就出现气短、呼吸急促、心悸、心前区疼痛、乏力、胸闷等症状', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14pxfont-family:宋体&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;预防保健措施：&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;&amp;nbsp; &amp;nbsp; 1&lt;span style=&quot;display: inline !important;&quot;&gt;、保持呼吸道通畅。通气障碍是肺心病加重的主要因素。所以，必须设法保持呼吸道通畅。痰咳不出，会加重呼吸道阻塞&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;蒸气或雾化吸入有利于湿润呼吸道，稀释稠痰，以利咳出&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;;&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;或用吸痰器不断将痰液吸出，保持呼吸道通畅。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;　　2&lt;span style=&quot;display: inline !important;&quot;&gt;、防止上呼吸道感染。肺心病急性发作多因上呼吸道感染而诱发。因此，凡有肺心病或慢性支气管炎的病人，都应严防上呼吸道感染。平时要加强锻炼，多到户外空气新鲜的环境中进行呼吸运动，增加肺活量，增强机体免疫力&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;;&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;注意御寒，防冷空气刺激。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;　　3&lt;span style=&quot;display: inline !important;&quot;&gt;、加强饮食调养。肺心病人呼吸所消耗的能量要比正常人大&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;10&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;倍，又因内脏淤血、水肿而食欲极差，吸收不佳。所以，多数病人营养不良，体重减轻，存在低蛋白血症，免疫力低下，容易导致感染，加重病情，以此形成恶性循环。因此，调节肺心病的饮食营养是十分重要的。应给病人以优质蛋白&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;蛋、奶、鱼等&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;、富含维生素、易消化的饮食，不要过度限盐，因为低盐可加重乏力、食欲不振，甚至恶心、呕吐，加重营养不良。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;　　4&lt;span style=&quot;display: inline !important;&quot;&gt;、减轻心脏负担。肺心病加重期约有&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;25%&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;70%&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;的病人发生心力衰竭，是肺心病死亡的重要原因。因此应想尽办法减轻心脏负担，保护最好心脏。病人应绝对卧床休息&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;;&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;不能平卧的，可取半坐位或前倾坐位，周围用被子垫最好，使病人感到舒服，不疲劳。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:28px&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;5&lt;span style=&quot;display: inline !important;&quot;&gt;、家庭吸氧治疗。肺心病加重期的氧疗原则是：长期、持续、低浓度加温湿化吸氧。一般应持续每天&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;16&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;小时以上，持续&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;4&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;周，间歇应在白天，睡眠时不要间断。近年，吸氧疗法已普及到家庭。家庭吸氧可使肺心病病死率由&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;60%&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;降至&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;20%&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;，效果明显。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14pxfont-family:宋体&quot;&gt;&amp;nbsp; &amp;nbsp; 预防调养：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;1.&lt;span style=&quot;display: inline !important;&quot;&gt;积极采取各种措施&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;包括宣传，有效的戒烟等&lt;/span&gt;&lt;span style=&quot;font-family: Calibri; display: inline !important;&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;display: inline !important;&quot;&gt;，提倡戒烟。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;2.&lt;span style=&quot;display: inline !important;&quot;&gt;积极防治原发病的诱发因素，如呼吸道感染，各种变应原，有害气体的吸入，粉尘作业等的防护工作和个人卫生的宣教。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;3.&lt;span style=&quot;display: inline !important;&quot;&gt;开展多种形式的群众性体育活动和卫生宣教，提高人群的卫生知识，增强抗病能力。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;4.&lt;span style=&quot;display: inline !important;&quot;&gt;宜进高热量，高蛋白易消化食物，有心衰者应控制钠，汞摄入，忌烟酒。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;5.&lt;span style=&quot;display: inline !important;&quot;&gt;生活规律，顺应自然，秋冬变节时注意保暖，避免受风寒诱发或加重病情。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;6.&lt;span style=&quot;display: inline !important;&quot;&gt;加强锻炼，提高机体抗病能力，积极治疗支气管及肺部疾患，防治感冒。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1, 1508121539);
INSERT INTO `dmt_feature` VALUES (4, '骨质疏松吃什么好?', '　1、何首乌粥?　　\n\n　　制何首乌30g，粳米100g，大枣3枚，冰糖适量。将何首乌放入锅内，加水适量，煎取浓汁，去渣备用。再将粳米、大枣、冰糖放入首乌汁中，加水适量，煎煮成粥食用。适用于肾阴虚型骨', '&lt;p&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　1&lt;span style=&quot;font-family:宋体&quot;&gt;、何首乌粥&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　制何首乌&lt;/span&gt;30g&lt;span style=&quot;font-family:宋体&quot;&gt;，粳米&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;100g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，大枣&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;枚，冰糖适量。将何首乌放入锅内，加水适量，煎取浓汁，去渣备用。再将粳米、大枣、冰糖放入首乌汁中，加水适量，煎煮成粥食用。适用于肾阴虚型骨质疏松症。&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　2&lt;span style=&quot;font-family:宋体&quot;&gt;、羊脊骨粥&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　羊脊骨&lt;/span&gt;(&lt;span style=&quot;font-family:宋体&quot;&gt;连尾&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;)1&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;具，茯苓&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;20g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，补骨脂粉&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;12g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，粳米&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;60g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，葱、姜、食盐各适量。将羊脊骨洗净剁成小块，与粳米加水煮粥至五成熟时，加入补骨脂粉搅匀，继续煎煮至熟，以葱、姜及食盐调味温服。适用于肾阳虚型骨质疏松症。&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　3&lt;span style=&quot;font-family:宋体&quot;&gt;、枸杞子羊肾粥&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　枸杞子&lt;/span&gt;30g&lt;span style=&quot;font-family:宋体&quot;&gt;，羊肾&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;1&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;只，肉苁蓉&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;15g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，粳米&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;60g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，食盐适量。将羊肾剖开，去掉内部筋膜，切碎，同枸杞子、粳米、肉苁蓉同放锅内，加水适量，温火煎煮至粥熟，食盐调味，温食。适用于肝肾阴虚型骨质疏松症。&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　4&lt;span style=&quot;font-family:宋体&quot;&gt;、参枣骨脂汤&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　党参&lt;/span&gt;20g&lt;span style=&quot;font-family:宋体&quot;&gt;，大枣&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;20&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;枚，补骨脂&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;15g&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。以上诸味，加水适量，煎煮两次，去渣，食枣喝汤。适用于脾肾阳虚型骨质疏松症。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　?5&lt;span style=&quot;font-family:宋体&quot;&gt;、鲤鱼汤&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　活鲤鱼&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;条，去鳞、鳃及内脏，加葱末、姜末、料酒和盐，稍腌片刻，加水煮至汤白鱼烂，分次饮用。适用于老年骨质疏松、肾炎水肿、黄疸性肝炎、肝硬化腹水、老年慢性支气管炎、哮喘、糖尿病等。&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　6&lt;span style=&quot;font-family:宋体&quot;&gt;、鲫鱼汤&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　活鲫鱼&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;条，去鳞、鳃及内脏，加葱末、姜末、料酒、盐等调料，稍腌片刻，加水煮至汤白鱼烂，分次食用。适用于老年骨质疏松、肾炎水肿、肝硬化腹水、糖尿病及痔疮脱肛等。&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　7&lt;span style=&quot;font-family:宋体&quot;&gt;、山药杞子甲鱼汤&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　怀山药&lt;/span&gt;10&lt;span style=&quot;font-family:宋体&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;15&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;克，枸杞子&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;10&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;克，甲鱼&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;1&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;只&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;500&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;克&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。甲鱼放入热水中宰杀，剖开洗净，去内脏，与各用料一起炖熟，加入姜、盐、酒少许调味，即可享用。有滋阴补肾、益气健脾功效。适用于阴虚偏胜的骨质疏松症患者。&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　　8&lt;span style=&quot;font-family:宋体&quot;&gt;、桑葚牛骨汤&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;?&lt;/span&gt;　　&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　桑葚子&lt;/span&gt;25&lt;span style=&quot;font-family:宋体&quot;&gt;克，牛骨&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;250&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;500&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;克。将桑葚子洗净，加酒、糖少许蒸制。另将牛骨置深锅中，水煮，开锅后撇去面上浮沫，加姜、葱再煮。见牛骨发白时，表明牛骨的钙、磷、骨胶等已溶解到汤中，随即捞出牛骨，加入已蒸制的桑葚子，开锅后再去浮沫，调味后即可饮用。此方有滋阴补血、益肾强筋之功效，适用于骨质疏松症、更年期综合征。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508120334);
INSERT INTO `dmt_feature` VALUES (5, '骨质疏松对于治疗的三大误区', '随着年龄的增长，老年人发生骨质疏松症的风险逐渐增加，由于骨质疏松 症会带来疼痛，并容易引发骨质疏松性骨折，使得老人们对骨质疏松心存恐惧，再加上广告上对补钙作用的夸大宣传，使许多老年人开始盲目补钙。其实', '&lt;p&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;&amp;nbsp; &amp;nbsp; 随着年龄的增长，老年人发生骨质疏松症的风险逐渐增加，由于骨质疏松&lt;/span&gt; &lt;span style=&quot;font-family:宋体&quot;&gt;症会带来疼痛，并容易引发骨质疏松性骨折，使得老人们对骨质疏松心存恐惧，再加上广告上对补钙作用的夸大宣传，使许多老年人开始盲目补钙。其实，老年人补钙过量，不但无益反而有害，造成这种局面的主要原因是老年人在认识上存在着三个误区。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14pxfont-family:宋体&quot;&gt;　　误区一：补钙能治好：许多老年人错误地认为，人老了，骨头脆了，所以要吃钙片来防治骨质疏松。其实不是这么回事。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　骨质疏松症是一种全身性的代谢性骨骼疾病，是人体衰老的表现。女性在绝经以后&lt;/span&gt;5&lt;span style=&quot;font-family:宋体&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;10&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;年，男性在&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;65&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;70&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;岁一般都会出现骨质疏松。无论是男性还是女性，一般在&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;30&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;～&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;35&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;岁达到一生中所获得的最高骨量，称为峰值骨量。此后骨质就开始丢失。由此可见，要想老来骨头硬朗，就得在&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;35&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;岁之前打好基础。底子厚了，到老年才剩得多。所以，老年人大量补钙并不能逆转骨量减少的趋势，也不可能治愈骨质疏松。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14pxfont-family:宋体&quot;&gt;　　误区二：不辩病因：骨质疏松主要分为两大类，即原发性的骨质疏松和继发性的骨质疏松。针对不同类型的骨质疏松，治疗手段也不一样，千万不能不加区分，一律补钙，否则会出现并发症。继发性的骨质疏松，如钙营养不良等引起的骨质疏松，补充钙剂就非常有效；而对于原发性的骨质疏松就不能依靠补钙来治疗。绝大多数老年人发生的骨质疏松属于原发性骨质疏松，这类老年人应该在医生的指导下进行治疗，盲目补钙没什么作用。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14pxfont-family:宋体&quot;&gt;　　目前国际上还没有什么有效手段能治愈骨质疏松，能做到的只是预防和减缓。像某些广告上宣传的那样，吃了某种补钙制剂，就能治愈骨质疏松，这是没有道理的。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　误区三：补钙越多越好：许多老人误认为，钙补得越多，吸收得也越多，形成的骨骼就越多。其实不是这样。通常，年龄在&lt;/span&gt;60&lt;span style=&quot;font-family:宋体&quot;&gt;岁以上的老年人，每天需要摄&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;800&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;毫克的钙。过量补钙并不能变成骨骼，如果血液中钙含量过高。可导致高钙血症，并会引起并发症，如肾结石、血管钙化等，危害老人健康。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1, 1508120401);
INSERT INTO `dmt_feature` VALUES (6, '秋季骨质疏松的饮食治疗', '骨质疏松是老年人的常见病,多发病，其主要表现是全身骨骼变得又薄又脆，强度及韧性下降，不少患者不仅腰酸背痛、容易疲倦，而且容易骨折。有些人稍不注意跌倒在地或旋转弯腰、轻微外伤均可引起骨折。这种情况下在腕', '&lt;p style=&quot;;line-height:28px&quot;&gt;&lt;span style=&quot;font-family: 宋体;font-family:宋体&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;&lt;span style=&quot;font-size:24px;font-size: 16px;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:16px&quot;&gt; 骨质疏松是老年人的常见病&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px&quot;&gt;,多发病，其主要表现是全身骨骼变得又薄又脆，强度及韧性下降，不少患者不仅腰酸背痛、容易疲倦，而且容易骨折。有些人稍不注意跌倒在地或旋转弯腰、轻微外伤均可引起骨折。这种情况下在腕关节、股骨颈尤为多见。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-family:宋体;font-size:16px&quot;&gt;　　体内的骨是一种有生命的活体组织，随着生命的存在，始终进行着新陈代谢活动。而钙和磷又是骨的主要成份。一般情况下以骨盐的形式集中于骨骼和牙齿中，从而维持了骨和牙齿中的硬度。钙和磷的极小一部分存在于血液和肌肉等软组织中，其含量保持一个适当的比值，从而维持了骨的健康。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　　对于中老年人来说，由于其各脏器也和骨关节一样发生老化，尤其是胃酸、胃蛋白酶和唾液腺分泌减少以及消化酶的活性降低，使其对钙的摄取、消化和吸收受到严重的影响，这时骨骼中的钙就会释放出来，以保持钙的正平衡状态。因此可以这样讲，骨质疏松的实质是指骨内钙的游离和减少。所以适量补钙对防治中老年人的骨质疏松是有益的&lt;/span&gt;,补钙不能治疗骨质增生。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-family:宋体;font-size:16px&quot;&gt;　　在日常膳食中，富含钙的食品有：乳类，虾皮、海带、豆制品以及绿色疏菜等。但在选用疏菜时要采取适当措施除去妨碍钙吸收利用的草酸等因素。其中天然螺旋藻含钙最为丰富。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508120489);
INSERT INTO `dmt_feature` VALUES (7, '如何预防骨质疏松？', '1、加强对骨质疏松高危人群的监测。如有遗传因素的人，过于消瘦的人，做了子宫卵巢切除、闭经早的人，嗜好烟酒的人，患有内分泌疾病以及长期服用皮质激素等药物长期卧床的人等，都属高危人群，要定期监测骨密度。如', '&lt;p&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&amp;nbsp; &amp;nbsp;1&lt;span style=&quot;font-family:宋体&quot;&gt;、加强对骨质疏松高危人群的监测。如有遗传因素的人，过于消瘦的人，做了子宫卵巢切除、闭经早的人，嗜好烟酒的人，患有内分泌疾病以及长期服用皮质激素等药物长期卧床的人等，都属高危人群，要定期监测骨密度。如果骨密度低于正常指标骨峰值均数&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;2.5&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;个标准差，就可以诊断为骨质疏松症，就要接受正规的骨质疏松治疗。单纯吃钙片是不够的，必须使用抑制骨吸收的药物，目的不是为了单纯提高骨密度，而是为了预防骨折。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　&amp;nbsp;&lt;span style=&quot;font-size: 16px; display: inline !important;&quot;&gt;2&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、运动可促进人体的新陈代谢。进行户外运动以及接受适量的日光照射，都有利于钙的吸收。运动中肌肉收缩、直接作用于骨骼的牵拉，会有助于增加骨密度。因此，适当运动对预防骨质疏松也是很有好处的。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　&amp;nbsp;&lt;span style=&quot;font-size: 16px; display: inline !important;&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、要养成良好的生活习惯。不抽烟，少喝酒，不喝浓茶，不食用过多的高蛋白食品。女性在绝经后有一个骨密度快速下降的时期，要注意补充雌激素。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-size: 16px; display: inline !important;&quot;&gt;　&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;span style=&quot;font-size: 16px; display: inline !important;&quot;&gt;　 4&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、要加强运动。有科学家发现，凡是长期坚持体育运动尤其是嗜好承重运动的人，其骨密度及强度明显高于同龄人，不论老少，极少出现自己跌倒后骨折的。对于长期从事办公室工作的人，无论多忙也要抽出时间运动一下肢体，能走路不要骑车，能骑车不要坐车，哪怕每天多走一段路，多爬一次楼梯，对骨骼健康都是有益的。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　&lt;span style=&quot;font-size: 16px; display: inline !important;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、吸烟会影响骨峰的形成，过量饮酒不利于骨骼的新陈代谢，喝浓咖啡能增加尿钙排泄、影响身体对钙的吸收，摄取过多的盐以及蛋白质过量亦会增加钙流失。&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size: 16px&quot;&gt;　&lt;span style=&quot;font-size: 16px; display: inline !important;&quot;&gt;6&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、要养成良好的生活习惯，避免酸性物质摄入过量，加剧酸性体质。如彻夜唱卡拉&lt;/span&gt;&lt;span style=&quot;font-family:Calibri&quot;&gt;OK&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;、打麻将、夜不归宿等生活无规律，都会加重体质酸化。应当养成良好的生活习惯，从而保持弱碱性体质，预防骨质疏松症的发生。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1508120585);
INSERT INTO `dmt_feature` VALUES (9, '慢性支气管炎吃什么好？', '慢性支气管炎是慢性病，除了药物治疗外，平时的保养也很重要。其中就包括饮食，那么，慢性气管炎患者吃什么好呢？下面就为大家推荐几个饮食偏方。', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;慢性支气管炎是慢性病，除了药物治疗外，平时的保养也很重要。其中就包括饮食，那么，慢性气管炎患者吃什么好呢？下面就为大家推荐几个饮食偏方。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;1.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;蒸梨：将梨洗净去核，再取川贝母&lt;/span&gt;3克、百部6克、陈皮6克洗净后，放入梨心中，上锅蒸熟，每日食用一个。蒸梨止咳化痰，对支气管炎的咳嗽、有痰不易咳出有较好的疗效。梨性味甘，微酸凉，入肺胃经，可生津润燥、清热化痰；川贝母润肺止咳化痰;百部温肺止咳；陈皮理气调中、燥湿化痰。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;2.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;银耳冰糖汤：选用银耳&lt;/span&gt;10克，黑木耳10克，用温水泡发，洗净与冰糖30克一起放入碗内，加水适量，放在蒸笼中蒸3小时即成。每次2～3汤匙，1日3次，能滋阴补肾，润肺止咳。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;3.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;贝母枇杷粥：大米&lt;/span&gt; 500 克、贝母 30 克、枇杷叶 30 克、冰糖 10 克。先将贝母去心，与枇杷叶共同研末，每次取 15 克和冰糖一起加入大米粥中调匀服食。适用于慢性支气管炎。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;4.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;金荞麦瘦肉汤：取猪瘦肉&lt;/span&gt;250克，金荞麦100克，冬瓜子30克，桔梗15克，生姜3片，红枣5枚。将猪肉洗净切块，沸水过水;金荞麦、冬瓜子、桔梗、红枣 （去核）洗净，放入炖盅内，加入温开水盖好，小火隔水炖3小时即可。可佐餐食用，每天1～3次，每次150～250毫升。适用于内有热毒（发热、咳嗽、痰多）者。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508121299);
INSERT INTO `dmt_feature` VALUES (10, '慢性支气管炎患者怎么保养？', '慢性支气管炎是当今生活中极其痛苦的一种疾病。是一种常见的呼吸系统疾病，不但伤害到了患者的身体健康，也给患者的生活、工作和学习带来影响。慢性支气管炎主要是以咳嗽、咳痰，或伴有哮喘及反复发作的慢性过程为主', '&lt;p style=&quot;text-indent:32px&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;慢性支气管炎是当今生活中极其痛苦的一种疾病。是一种常见的呼吸系统疾病，不但伤害到了患者的身体健康，也给患者的生活、工作和学习带来影响。慢性支气管炎主要是以咳嗽、咳痰，或伴有哮喘及反复发作的慢性过程为主要症状。少数人是由急性支气管炎未治愈而转为慢性支气管炎，大多数是隐潜发病。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;慢性支气管炎的主要病因有细菌感染、刺激性烟雾、粉尘、大气污染、吸烟、寒冷刺激、对花粉等过敏。本病对发生在中老年，男性多于女性，病情发展缓慢，严重时可并发阻塞性肺气肿甚至慢性原发性心脏病，是一种危害身体健康的常见病。那么，慢性支气管炎除了日常的药物治疗外，该如何保养呢？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;一、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;戒烟。慢性支气管炎患者不但要首先戒烟&lt;/span&gt;,而且还要避免被动吸烟,因为烟中的化学物质如焦油、尼古丁、氰氢酸等,可作用于植物神经,引起支气管的痉挛,从而增加呼吸道阻力。研究表明长期吸烟者，该病发生率较不吸烟者高2～8倍，吸烟时间越长、量越大、患病率越高。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;二、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;注意保暖。在气候变冷的季节&lt;/span&gt;,患者要注意保暖,避免受凉,因为寒冷一方面可降低支气管的防御功能,另一方面可反射地引起支气管平滑肌收缩、粘膜血液循环障碍和分泌物排出受阻,可发生继发性感染。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;三、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;加强锻炼。慢性支气管炎患者在缓解期要作适当的体育锻炼，以提高机体的免疫能力和心、肺的贮备能力。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;四、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;预防感冒。感冒也会加重病情。及时注意天气变化，不要吹风、淋雨雪。也可通过作抗寒锻炼，提高身体素质，达到预防感冒的目的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;五、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;避免烟尘。外出时及时观察周围环境，避免烟雾、粉尘和刺激性气体对呼吸道的环境影响，以免诱发慢性支气管炎。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;六、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;预防过敏。过敏也会刺激呼吸道，诱发或者加重病情。平时不要吃易过敏的食物，家里也不能养花草及带毛的小动物。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1, 1508121401);
INSERT INTO `dmt_feature` VALUES (11, '秋季如何预防哮喘', '一场秋雨一场凉，秋季昼夜温差大，后半夜气温比较低防备不好容易受凉感冒从而引发哮喘，其次，灰尘中大量存在的螨虫容易生长繁殖，有些人由于吸入含螨的灰尘而诱发哮喘。此外，秋季时，一些花粉漂浮于空气中，有过敏', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;&amp;nbsp; &amp;nbsp; 一场秋雨一场凉，秋季昼夜温差大，后半夜气温比较低防备不好容易受凉感冒从而引发哮喘，其次，灰尘中大量存在的螨虫容易生长繁殖，有些人由于吸入含螨的灰尘而诱发哮喘。此外，秋季时，一些花粉漂浮于空气中，有过敏体质的人吸入也可引发支气管哮喘。天气一凉就进入了哮喘的高发期，给哮喘病患者带来了很多痛苦。那么，我们应该怎么预防秋季哮喘病发作呢。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:26px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;一、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;做好防寒保暖。保暖很重要，哮喘病患者应该根据天气变化及时增加衣物，但是切忌穿的太暖，太暖了也会导致哮喘病发作。在衣物的选择上，尽量选择纯棉织品，而且面料要光滑、舒适、柔软，切不可穿化纤织品、有刺激性气味及严重掉色的衣服。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:26px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;二、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;饮食以清淡为主。多吃梨、苹果、葡萄、柚子、萝卜、青菜、莲藕、木耳等水果和蔬菜，辛辣刺激的就不要吃了。像螃蟹、虾、海鱼、蛋及蛋制品、奶及奶制品等易过敏的食物不宜食用，过敏也能诱发哮喘。另外多喝水，如果喝酒的话以后也不要喝了。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:26px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;三、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;改善居住环境。经常开窗换气，保持室内空气清新。家具也尽量选择简洁干净的。少用地毯、挂毯，床褥也选择新棉花做的，床单被罩也要选择棉的，并且要经常拿出来晒太阳，杀菌。家里也不要养带毛的动物，不养花草。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:26px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;四、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;进行体育锻炼和耐寒训练。坚持做一些运动，像散步、打太极、慢跑、游泳都可以，激烈的不行，容易刺激哮喘病发作。经常运动能提高身体素质，增强对疾病的抵抗力。耐寒训练也不可少，如用冷水洗脸、洗手和搓擦鼻部，身体状况允许时还可用冷水擦身体。但耐寒锻炼应量力而行，持之以恒。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:26px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;五、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;服用御灵菲宝提高呼吸道和肺部的免疫力，免疫力高了，哮喘自然就不容易发作了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1508121977);
INSERT INTO `dmt_feature` VALUES (12, '哮喘病患者适宜吃什么', '哮喘病是一种比较顽固的疾病，如果忽视治疗，可以带来严重的后果。如果有哮喘病，就应该在平时的饮食上多加留意了，得知道哮喘病是以吃什么？', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;&amp;nbsp; &amp;nbsp;哮喘病是一种比较顽固的疾病，如果忽视治疗，可以带来严重的后果。如果有哮喘病，就应该在平时的饮食上多加留意了，得知道哮喘病是以吃什么？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:8px&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1）水果的选择：食用适量的梨、柑橘、枇杷、核桃仁、香蕉及芝麻、蜂蜜等物，有助于大便畅通、腹压下降，可防哮喘发作。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:8px&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2）哮喘患者可以适当的选用蛋白质含量较高又有丰富维生素的食品，如奶制品、蛋类、肉汁等。 &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;3）主食及豆类的选择：热量要求与正常人相近，并无特殊要求，对提供热量的主要食物，可按照病人平时的饮食量进食，多食豆腐等大豆制品。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;4）也可适当补充含镁食物，如海带、芝麻、核桃、花生、大豆及绿叶蔬菜等。含锌食物有肉、家畜内脏、麦芽、小扁豆等。含钙食物有山楂、柑橘、杏仁、瓜子、核桃等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;5）其他适宜哮喘患者吃的食物有:丝瓜、白萝卜、南瓜、冬瓜、山药、百合等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;中医强调&lt;/span&gt;“五谷为养，五果为助，五畜为益，五菜为充”。药补不如食补，合理科学的调配饮食，就能全面、均衡获得人体所需的基本营养物质，对缓解哮喘病有着很大的帮助。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508122023);
INSERT INTO `dmt_feature` VALUES (13, '哮喘患者有什么食物不能吃？', '哮喘虽然是我们日常生活中的常见病，但是大家都知道哮喘患者的饮食原则吗？知道哮喘患者不能吃什么食物吗？', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;&amp;nbsp; &amp;nbsp; 哮喘虽然是我们日常生活中的常见病，但是大家都知道哮喘患者的饮食原则吗？知道哮喘患者不能吃什么食物吗？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;1、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;水果与蔬菜。一有什么病，病人在饮食方面就会联想到水果、蔬菜，认为多吃水果、蔬菜这些清淡的食物能帮助病情尽快地恢复。其实，水果与蔬菜并不是百利而无一害，研究表明，有少数水果与蔬菜是可能会诱发过敏性哮喘的，如水果有桃子、苹果、香蕉、草莓、樱桃、椰子等，蔬菜有蘑菇，番茄、辣椒、韭菜、茄子、白菜、大蒜、香椿、蕨菜等，这些食物容易加重哮喘病情，所以患者应该提高警惕，尽量少吃。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;2、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;海产品及水产品。这些食物的致敏原通常耐热，其熟食品也常常诱发过敏性哮喘，特别是食用不新鲜的海产品可使过敏性哮喘的发生率明显增加。因此，各类海产品及水产品如鱼、虾、蟹、蛤及贝类等，应在新鲜时供人食用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;3、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;鸡蛋。鸡蛋诱发过敏性疾病的病例不少，其中有少部分有为过敏性哮喘的表现。有医学专家表示，鸡蛋的蛋清中含有诱发人体过敏的主要致敏原，这种致敏原叫卵蛋白，容易诱发哮喘。所以，我们在吃鸡蛋的时候，可以选择只吃蛋黄，而不吃蛋白。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;4、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;奶及奶制品。以牛奶致儿童过敏性哮喘发生率为最高。牛奶中含有甲、乙两种乳白蛋白和酪蛋白等成分，其中甲种乳白蛋白是所有牛奶成分中最强的致敏源，尽&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;管这种蛋白经加热处理后，其致敏性可明显减弱，但对于高度牛奶过敏者来说仍是较强的致敏原，可以诱发哮喘发作。因此，对需要乳类喂养的婴幼儿来说，应积极主张母乳喂养，以减少牛奶喂养所致过敏性哮喘的发生机会。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;5、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;油料作物。花生、芝麻等食品属于可以榨油的油料作物食品，这些食品含有较高的蛋白质，而蛋白质又与哮喘疾病有关系，平时如果油料食品吃得多，是较容易诱发哮喘病的，所以我们应少吃。不过，有研究表明，如果像花生、芝麻这些油料作物榨成油，是可以减少哮喘疾病的发生的。所以，小编建议平时要是吃花生，最好把它们煮熟后再吃。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;6、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;豆类食品。目前证实黄豆、绿豆、红豆、黑豆以及芸豆、青豆等多种豆类也可诱发过敏性哮喘症状，其也是与豆类含有较高蛋白质有关。炒熟煮透后其致敏性降低，故食用前应充分热加工。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;总之，哮喘病是一种反复发作且难以根治的病，我们平时应该积极预防。如果患了哮喘病，我们在药物治疗的同时还应配合食疗，我们只有知道了能吃哪些食物，不能吃哪些食物才能对我们的病情有帮助。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508122078);
INSERT INTO `dmt_feature` VALUES (14, '预防秋季哮喘发作', '哮喘的发作，一直被认为与气象因素关系极大。据临床统计，天气变化占哮喘病发作原因的四分之三以上，其次为疲劳、过敏、饮食不当等因素。从时间上看，每年的九十月份是哮喘病的高发季节，其次是冬季、初春和梅雨时节', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一、注意饮食&lt;/span&gt; &amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;平时要养成不挑食、平衡饮食的好习惯。另外，秋季气候干燥，大家常常会有口干、皮肤干燥等燥热表现。中医认为肺喜润恶燥，所以应多吃润燥食品，如梨、荸荠、柚、甘蔗等，对过敏性哮喘有预防作用。另外，注意不要多吃大闸蟹等高蛋白食物。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;二、锻炼身体&lt;/span&gt; &amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;平时，我们就应加强体育锻炼。多做运动，增强体质，这是预防各种疾病的最有利措施，特别是对体弱和过敏体质的人群来说。良好的身体状况和家居环境有利于预防疾病。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;三、改善家居环境&lt;/span&gt; &amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;改善居住环境对预防过敏性哮喘也很重要。实验表示，大量的过敏性疾病都与家具中的尘螨有关系。特别是地毯、沙发、床铺等，更是尘螨寄居的最佳场所。因此，针对这一情况，大家在平时应当多开窗通风，家居用品尽量少选择一些羊毛制品，同时，经常给家里晒太阳，患者的内衣物还可以用开水烫，以减少尘螨的存在。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;四、避开过敏原&lt;/span&gt; &amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;秋季是外出游玩的好季节。户外活动能够锻炼身体、呼吸新鲜空气，但与此同时，尘螨、动物皮毛、霉菌孢子，以及螃蟹等食物，都有可能成为诱发过敏病的&lt;/span&gt;“罪魁祸首”。所以大家在赏秋游玩的同时也一定要做好防护工作，如戴口罩，少去人多的地方，远离宠物等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:14px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;哮喘的发作，一直被认为与气象因素关系极大。据临床统计，天气变化占哮喘病发作原因的四分之三以上，其次为疲劳、过敏、饮食不当等因素。从时间上看，每年的九十月份是哮喘病的高发季节，其次是冬季、初春和梅雨时节。同时，刚刚到来的秋季，是一年中冷空气活动较为频繁的季节，每次冷空气过后，都伴随着气温、气压、降水、空气湿度的明显变化。尤其是气温的降低直接对哮喘有诱发作用。因为依中医理论，哮喘一病，宿根为&lt;/span&gt;“痰饮伏于内，胶结不去”。一旦气候变化，就容易引动发病。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1508122114);
INSERT INTO `dmt_feature` VALUES (15, '如何预防支气管炎？', '秋季是支气管炎的高发季节，抵抗力差的人很容易感染。一旦感染给我们的身体、生活、还有工作都带来很多不好的影响，那么有没有好的方法可以预防支气管炎发病呢？', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;秋季是支气管炎的高发季节，抵抗力差的人很容易感染。一旦感染给我们的身体、生活、还有工作都带来很多不好的影响，那么有没有好的方法可以预防支气管炎发病呢？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:25px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;一、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;饮食宜忌。支气管炎的饮食以清淡为主。可多吃些易消化的食物，如面条、稀粥、面汤、牛奶等，还应多吃梨、柚子、橘子等具有止咳化痰作用且富含维生素的水果，多吃蔬菜。辛辣刺激的食物对气管刺激很大，尽量少吃或者不要吃。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:25px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;二、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;保持良好的家庭环境卫生。保持室内空气流通，经常开窗换气。经常打扫卫生，屋里不要有灰尘。外出时要尽量带口罩，注意避开有粉尘、刺激性气体的地方。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:25px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;三、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;坚持锻炼身体。经常锻炼身体，能增强体质，提高抵抗力，防止呼吸道感染，可预防或减少本病发生。但是锻炼身体适度就行，不可过量，而且尽量选择慢运动，比如打太极、散步、慢跑等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:25px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;四、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;预防感冒。注意天气及季节变化，及时增减衣物，避免感冒，能有效地预防支气管炎发作。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:25px&quot;&gt;&lt;span style=&quot;font-family:Calibri;font-size:16px&quot;&gt;五、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;戒烟。吸烟也是引发支气管炎的重要因素，为了自己的身体健康，如果吸烟的话就要慢慢戒掉了，不吸烟的朋友也要拒绝二手烟。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;其实很多疾病只要我们平时生活中注意点是可以避免的。没有病的要学会预防，平时养成良好的生活习惯；有病的要及时就医，以免耽误病情。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 2, 1, 1508122176);
INSERT INTO `dmt_feature` VALUES (16, '哮喘突然发作你知道该怎么做吗？', '哮喘是一种慢性病，不发作的时候跟正常人一样，可一旦发作起来后果不堪设想，有可能会危及生命。但是你知道哮喘发作起来应该怎么做吗？哮喘发作时的急救措施有哪些？', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;哮喘是一种慢性病，不发作的时候跟正常人一样，可一旦发作起来后果不堪设想，有可能会危及生命。但是你知道哮喘发作起来应该怎么做吗？哮喘发作时的急救措施有哪些？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;一、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;吸氧疗法&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;让患者半卧位，消除恐惧心理和焦虑情绪，但慎用镇静剂。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;中、重度哮喘发作时，由于呼吸道阻塞，肺泡通气不足，造成机体明显缺氧，必须及时补充氧气。当病人已出现严重呼吸困难、口唇、指甲青紫时更应尽快吸氧。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1）使用氧气袋（氧气枕头）或压缩氧气钢瓶，平时灌好氧气备用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2）中度缺氧吸入25%～30%的氧浓度，相当于氧流量1～2升/分。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;3）氧气吸入前应当通过一个水封瓶适当加温、加湿，以免氧气过干过冷对呼吸道的刺激，并可稀释痰液以利排痰。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;二、气喘喷雾剂疗法&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;　立于不败之地即吸入手边备用的气喘喷雾剂，这种喷雾能在他们病情发作时起到很好的缓解作用。专家介绍，这种哮喘发作时使用的支气管扩张药物可以说是哮喘患者的&lt;/span&gt;“救命草”，能迅速扩张狭窄气道，缓解喘息、呼吸困难、胸闷、咳嗽等症状，必须随时随地携带，是哮喘急性发作的首选用药。但是，必须得注意用量，急性哮喘发作时使用气雾剂1~2喷，一般不超过6~8喷，如果过量吸入，可以导致严重心律失常而有生命危险，正确的吸入法是治疗的关键。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;三、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;向&lt;/span&gt;“120”急救中心呼救&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;中度以上哮喘初次发作，可向&lt;/span&gt;“120”急救中心呼救，请急救医生前来救治。待病情稳定后，护送病人到医院就诊。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;通过以上对哮喘发作的急救措施的相关知识介绍，大家是不是对哮喘的急救有了全新的认识呢？哮喘的突发性是可怕的，我们只要做好防范措施，就可以避免出现危险情况。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 1, 1508122272);
INSERT INTO `dmt_feature` VALUES (17, '肺气肿患者吃什么好？', '肺气肿是指终末细支气管远端的气道弹性减退，过度膨胀、充气和肺容积增大或同时伴有气道壁破坏的病理状态。按其发病原因肺气肿有如下几种类型：老年性肺气肿、代偿性肺气肿、间质性肺气肿、灶性肺气肿、旁间隔性肺气', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;肺气肿是指终末细支气管远端的气道弹性减退，过度膨胀、充气和肺容积增大或同时伴有气道壁破坏的病理状态。按其发病原因肺气肿有如下几种类型：老年性肺气肿、代偿性肺气肿、间质性肺气肿、灶性肺气肿、旁间隔性肺气肿、阻塞性肺气肿。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;肺气肿也是临床上的一种常见病，虽然很难治愈，但是如果得到有效的治疗，合理的饮食调理的话，病情完全可以得到控制，仍可基本承受工作和生活的能量消耗。既然说到了饮食，肺气肿患者吃什么好呢？&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;肺气肿患者的饮食原则&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;　1、肺气肿多见于老年患者，他们的体质较弱，所以膳食中应以流质和半流质的主食为主。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;　2、如果患者伴有气短、喘气、咳痰等症状，应该多吃温热的食物。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;　3、可多吃有助清热化痰、润肺止咳的食物。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;适合的饮食&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;1、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;饮食清淡为主。新鲜的水果和蔬菜多吃，如各种青菜、萝卜、丝瓜、冬瓜、梨、柚子、枇杷、香蕉等，也可经常喝蜂蜜水。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;2、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;供给充足的蛋白质和铁。应多吃瘦肉、动物肝脏、豆制品。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;3、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;多吃含有维生素&lt;/span&gt;A、C及钙的食物。如蛋黄、胡萝卜、南瓜、大枣、潘茄、青椒、豆腐、芝麻酱等。奶及奶制品虽然含钙高，却不宜多吃。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;4、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;多喝水。大量喝水，有利于痰液稀释。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;5、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;多食用菌类。如香菇、蘑菇等，能调节免疫功能。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;肺气肿患者应保持心情舒畅，合理饮食，积极治疗，才能早日康复。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508122315);
INSERT INTO `dmt_feature` VALUES (18, '秋季养生菜', '秋季虽然是丰收的季节，但是也是各种疾病的高发季节，所以秋季养生很重要。那么秋季养生都适合做什么吃呢？', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;秋季虽然是丰收的季节，但是也是各种疾病的高发季节，所以秋季养生很重要。那么秋季养生都适合做什么吃呢？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;一、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;西红柿炒菜花&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;原料：菜花、西红柿、油、蒜、盐、味精。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;做法：菜花用水冲洗干净，手掰成小朵，在开水中烫两分钟后捞出控干。起油锅，油热后蒜末爆锅，下入西红柿翻炒，等西红柿软烂后加入菜花，大火翻炒&lt;/span&gt;2分钟，加入适量盐和味精，出锅即可。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;功效：秋季是干燥的季节，皮肤很容易缺水破皮，西红柿、菜花都含有丰富的维生素&lt;/span&gt;C，秋季食用非常合适。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;二、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;莲藕排骨汤&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;原料：莲藕、猪排骨，黄酒、生姜、葱、盐少许。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;做法：猪排骨切段，飞水；将藕洗净切片，与排骨同置&lt;/span&gt;1500毫升温水之中，加黄酒、姜、葱少许，水沸后文火炖90分钟左右，加少许盐，即可食用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;功效：：熟藕性味甘温，可开胃、滋补、生肌、止泻、固精、养胃滋阴、健脾益气的功效，此汤可帮助人们缓解秋燥。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;三、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;百合炒虾仁&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;原料：百合、虾仁&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;做法&lt;/span&gt;: 将百合剥成瓣洗净,沥干;荷兰豆去角筋洗净切成两段;将虾只去虾仁,去虾线,洗净。将处理干净的虾仁放入小碗中,加入盐,胡椒粉搅匀;将荷兰豆放入开水中焯一下快速取出,然后放入凉水中浸泡,激凉保持翠绿。炒锅倒入适量油,烧至五成热时,下入虾仁划散,变色后捞出。炒锅留底油,烧热后下入葱末炒香,加入虾仁,烹入料酒,加入百合,盐,糖,生抽,胡椒粉及少许鲜汤,翻炒片刻待熟,放入荷兰豆炒匀,即可出锅。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;功效：百合有很高的药用价值。解毒、理脾健胃、利湿消积、宁心安神、促进血液循环等功效，而虾仁清淡爽口，易于消化。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;四、&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;银耳莲子羹&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;原料：干银耳&lt;/span&gt;15克，莲子50克，枸杞5克，冰糖100克。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;做法：干银耳泡水&lt;/span&gt;2个小时，拣去老蒂及杂质后撕成小朵，加水4杯入蒸笼蒸半个小时取出备用。莲子煮熟;将银耳、莲子、枸杞加入开水放入炖盅中，加冰糖放入蒸笼蒸半个小时即可食用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;功效：银耳有润肺、养胃、生津、益气、补脑、强心之功效。莲子有益心、补肾、止泻、安神之效。银耳莲子羹浓甜润滑，美味可口，有较强的滋补健身功能&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508122361);
INSERT INTO `dmt_feature` VALUES (19, '哮喘病吃什么水果好', '现在已经是秋天了，天也会一天比一天凉，对哮喘病患者来说也不是什么好事。大家都知道秋季是哮喘病的高发季，那么，对于哮喘病我们除了药物治疗外，饮食调养也是一个很不错的方法。可是饮食调养的方法也有很多，我最', '&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;现在已经是秋天了，天也会一天比一天凉，对哮喘病患者来说也不是什么好事。大家都知道秋季是哮喘病的高发季，那么，对于哮喘病我们除了药物治疗外，饮食调养也是一个很不错的方法。可是饮食调养的方法也有很多，我最喜欢的还是吃水果，既省时又省力。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;1.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;梨&lt;/span&gt; &lt;span style=&quot;font-family:宋体&quot;&gt;梨味甘、微酸、性偏凉，主要归肺、胃二经，具有润肺清热、消痰降火、清胃泻热、养阴生津、滋肾补虚及润肠通便等作用。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;2.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;桔子其性味甘，酸而湿&lt;/span&gt;.无毒。橘实归肺、胃、心、肝等经，橘叶、橘核、橘络等也可药用，其有润肺止咳、开胃生津、健脾止泻、行气宣痹及疏肝解郁等作用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;3.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;柚子柚子是去火极好的水果，经常吃柚子，对身体很好。柚子虽然味稍微有点苦，但具有润肺止咳、助消化的功效。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px&quot;&gt;&lt;span style=&quot;font-family:宋体;font-size:16px&quot;&gt;4.&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;适合哮喘病吃的水果还有樱桃、苹果、西瓜、荔枝、蜜桃、香蕉等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-left:24px;text-indent:0&quot;&gt;&lt;span style=&quot;;font-family:宋体;font-size:16px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;;font-family:宋体;font-size:16pxfont-family:宋体&quot;&gt;水果虽然不能帮我们治愈哮喘，但是润肺止咳的功效还是不错的，多吃点对我们的病没有害处。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 3, 1, 1508122407);

