-- ----------------------------
-- Table structure for dmt_section
-- ----------------------------
DROP TABLE IF EXISTS `dmt_section`;
CREATE TABLE `dmt_section`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `cname` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `pid` smallint(10) NOT NULL,
  `path` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `intro` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `show` tinyint(11) UNSIGNED NOT NULL DEFAULT 1,
  `addtime` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 14 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of dmt_section
-- ----------------------------
INSERT INTO `dmt_section` VALUES (1, '骨病', 0, '1', '德欣堂中医馆聘请多位资深骨科中医结合近百年的临床治疗经验，从根源治疗风湿骨病，内服扶正祛邪，固肾养肝，外敷，舒经活络，祛风除湿。根据患者不同的体质与病情一人一方，据病下药从病因病机出发，不苟同于现今一药治百病，一药治千人的流水线式治疗思路。德民堂一人一方治疗骨病真正做到作用直接，疗效确切，调补五脏，标本兼顾。\r\n   常见骨病病种：风湿、类风湿性关节炎、股骨头坏死、腰间盘突出、椎管狭窄、腰椎骨质增生、椎体滑脱、膝关节退行性病变、骨质增生、滑膜炎、半月板损伤、髌骨软化、颈椎骨质增生、颈椎间盘突出、颈椎曲度变', 1, '1506736794');
INSERT INTO `dmt_section` VALUES (5, '肠胃病', 0, '5', '德欣堂回归传统中医治疗胃肠病，郭保磊医生带领多为资深中医师长期坐诊，结合百年组方，根据现代人体质加减配伍，从病因病机出发，脾胃同治五脏同调，从疾病根源治疗慢性肠胃病、见效快、周期短、不复发。深受患者好评。\r\n   常见肠胃病种：慢性胃炎、浅表性胃炎、红斑性胃炎、反流性胃炎、非萎缩性胃炎、胃溃疡、糜烂性胃炎、十二指肠溃疡。', 1, '1507780266');
INSERT INTO `dmt_section` VALUES (10, '德欣堂骨病科室', 1, '1,10', '德欣堂中医馆聘请多位资深骨科中医结合近百年的临床治疗经验，从根源治疗风湿骨病，内服扶正祛邪，固肾养肝，外敷，舒经活络，祛风除湿。根据患者不同的体质与病情一人一方，据病下药从病因病机出发，不苟同于现今一药治百病，一药治千人的流水线式治疗思路。德欣堂一人一方治疗骨病真正做到作用直接，疗效确切，调补五脏，标本兼顾。\r\n   常见骨病病种：风湿、类风湿性关节炎、股骨头坏死、腰间盘突出、椎管狭窄、腰椎骨质增生、椎体滑脱、膝关节退行性病变、骨质增生、滑膜炎、半月板损伤、髌骨软化、颈椎骨质增生、颈椎间盘突出、颈椎曲度变', 1, '1507793440');
INSERT INTO `dmt_section` VALUES (11, '咳喘', 0, '11', '德欣堂治疗咳喘完全杜绝西医，已万鸿晟教授八字诀“清肺补肾，补肺固肾”为基础，结合德欣堂百年祖方，在张勇医生的领导下，为中医治疗咳喘类疾病树立了典范，真正做到了，治疗彻底，无副作用。患者对张勇医生口碑极佳。\r\n    常见呼吸道病种：急慢性支气管炎、咳喘、哮喘、肺心病、肺气肿、肺纤维化。', 1, '1507793501');
INSERT INTO `dmt_section` VALUES (12, '德欣堂咳喘科室', 11, '11,12', '德欣堂治疗咳喘完全杜绝西医，已万鸿晟教授八字诀“清肺补肾，补肺固肾”为基础，结合德欣堂百年祖方，在张勇医生的领导下，为中医治疗咳喘类疾病树立了典范，真正做到了，治疗彻底，无副作用。患者对张勇医生口碑极佳。\r\n    常见呼吸道病种：急慢性支气管炎、咳喘、哮喘、肺心病、肺气肿、肺纤维化。', 1, '1507793577');
INSERT INTO `dmt_section` VALUES (13, '德欣堂肠胃病科室', 5, '5,13', '德欣堂回归传统中医治疗胃肠病，郭保磊医生带领多为资深中医师长期坐诊，结合百年组方，根据现代人体质加减配伍，从病因病机出发，脾胃同治五脏同调，从疾病根源治疗慢性肠胃病、见效快、周期短、不复发。深受患者好评。\r\n   常见肠胃病种：慢性胃炎、浅表性胃炎、红斑性胃炎、反流性胃炎、非萎缩性胃炎、胃溃疡、糜烂性胃炎、十二指肠溃疡。', 1, '1507793608');

-- ----------------------------
-- Table structure for dmt_system
